function gerarCardsProjetos(e){const o=e.map(e=>{const o=Math.round(e.arrecadado/e.meta*100);return`\n            <div class="card col-lg-4 col-md-6">\n                <img src="${e.imagem}" alt="Imagem do projeto: ${e.nome}" class="card-img">\n                <div class="card-content">\n                    <h3>${e.nome}</h3>\n                    <p>${e.descricao}</p>\n                    <p>Meta: R$ ${e.meta.toFixed(2).replace(".",",")}</p>\n                    <p>Arrecadado: R$ ${e.arrecadado.toFixed(2).replace(".",",")}</p>\n                    \n                    <div style="background-color: #e0e0e0; border-radius: 4px; overflow: hidden; margin: 10px 0;">\n                        <div style="width: ${o}%; background-color: var(--cor-secundaria-base); height: 20px; text-align: center; color: white;">\n                            ${o}%\n                        </div>\n                    </div>\n                    \n                    <a href="cadastro.html" class="btn btn-secondary">Quero Doar</a>\n                </div>\n            </div>\n        `});return o.join("")}function renderizarConteudo(e){const o=document.getElementById("main-content");if(templates[e]){o.innerHTML=templates[e];const a=document.getElementById("main-navigation");a.classList.remove("menu-aberto")}else o.innerHTML="<h2>Página Não Encontrada</h2>"}const projetos=[{id:"p1",nome:"Capacitação Digital para Idosos",descricao:"Oferece aulas básicas de informática, internet e segurança digital para pessoas acima de 60 anos.",meta:5e3,arrecadado:3200,imagem:"assets/images/tech.jpg"},{id:"p2",nome:"Programa de Mentoria de Código",descricao:"Conecta jovens talentos de comunidades carentes com desenvolvedores sêniores do mercado.",meta:8e3,arrecadado:1500,imagem:"assets/images/mentoria.jpeg"},{id:"p3",nome:"Reforma da Biblioteca Comunitária",descricao:"Campanha para modernizar o espaço físico e adquirir novos computadores e livros didáticos.",meta:12e3,arrecadado:12e3,imagem:"assets/images/codekids.jpg"}],templates={home:'\n        <section>\n            <h2>Quem Somos e Nossa História</h2>\n            <div class="grid">\n                <article class="col-lg-8">\n                    <h3>Nossa Missão e Visão</h3>\n                    <p><strong>Missão:</strong> Promover a inclusão digital e social de jovens em comunidades de baixa renda, oferecendo capacitação em tecnologia e acesso a oportunidades de emprego. </p>\n                    <p><strong>Visão:</strong> Ser a principal ponte entre talentos de comunidades carentes e o mercado de trabalho digital no Brasil.</p>\n                    <p>A ONG foi fundada em 2018 e desde então dedicamos nossos esforços a Educação, Tecnologia e Inclusão Social.</p>\n                </article>\n                <div class="col-lg-4">\n                    <img src="assets/images/equipe-feliz.jpg" alt="Foto da equipe de voluntários da ONG sorrindo." style="width: 100%;">\n                </div>\n            </div>\n        </section>\n\n        <section>\n            <h2>Faça a Diferença Conosco</h2>\n            <p>Seja nosso parceiro e ajude-nos a ampliar nosso impacto. Explore como você pode contribuir:</p>\n            <ul>\n                <li><a href="#projetos" class="spa-link">Ver Oportunidades de Voluntariado</a></li>\n                <li><a href="#doacao" class="spa-link">Saiba Como Doar</a></li>\n            </ul>\n        </section>\n    ',projetos:`\n        <h2>Projetos Atuais e Doações</h2>\n        <p class="text-center">Apoie um de nossos projetos sociais e transforme vidas.</p>\n        <div class="grid" id="lista-projetos">\n            ${gerarCardsProjetos(projetos)}\n        </div>\n    `,cadastro:'\n        <p class="text-center">Por favor, utilize o link direto para o <a href="cadastro.html">Formulário de Cadastro/Login</a>.</p>\n    ',doacao:'\n        <h2>Informações sobre Doação</h2>\n        <p>Toda contribuição financeira é revertida integralmente para os projetos sociais. Use o nosso PIX.</p>\n        <p><strong>PIX:</strong> 27.982.452/0001-20 (CNPJ)</p>\n        <p><a href="#home" class="spa-link">Voltar para a Home</a></p>\n    '};document.addEventListener("DOMContentLoaded",function(){const e=window.location.hash.substring(1)||"home";renderizarConteudo(e);const o=document.querySelectorAll(".spa-link");o.forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const o=this.getAttribute("href").substring(1);renderizarConteudo(o)})});const a=document.querySelector(".menu-toggle"),n=document.getElementById("main-navigation");a&&n&&a.addEventListener("click",function(){n.classList.toggle("menu-aberto");const e="true"===a.getAttribute("aria-expanded")||!1;a.setAttribute("aria-expanded",!e)})});